import React, { useState } from 'react';
import axios from 'axios';

const UserForm = () => {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [image, setImage] = useState(''); // New state for the base64 image

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/users', {
        name,
        address,
        email,
        mobileNumber,
        image,
      });

      console.log('User data saved:', response.data);
      // Clear the form fields after successful submission
      setName('');
      setAddress('');
      setEmail('');
      setMobileNumber('');
      setImage('');
    } catch (error) {
      console.error('Error saving user data:', error);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check if the file size is less than or equal to 25KB (25 * 1024 bytes)
      if (file.size <= 25 * 1024) {
        const reader = new FileReader();
        reader.onload = (event) => {
          const base64Image = event.target.result;
          setImage(base64Image);
        };
        reader.readAsDataURL(file);
      } else {
        alert('Image size should be less than or equal to 25KB');
      }
    }
  };

  return (
    <div>
      <h2>Enter User Information</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Mobile Number:</label>
          <input type="tel" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} />
        </div>
        <div>
          <label>Image:</label>
          <input type="file" accept=".jpg, .jpeg, .png" onChange={handleImageChange} />
          {image && (
            <img src={image} alt="User Image" width="100" style={{ marginTop: '10px' }} />
          )}
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default UserForm;
