import React, { useState } from 'react';
import axios from 'axios';

const UserForm = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState('');
  const [college, setCollege] = useState('');
  const [subject, setSubject] = useState('');
  const [location, setLocation] = useState('');
  const [mode, setMode] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [email, setEmail] = useState('');
  const [experience, setExperience] = useState('');
  const [qrCode, setQrCode] = useState('');
  const [transaction, setTransaction] = useState('');
  const [agree, setAgree] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/users', {
        name,
        description,
        image,
        college,
        subject,
        location,
        mode,
        mobileNumber,
        email,
        experience,
        qrCode,
        transaction,
        agree,
      });

      console.log('User data saved:', response.data);
      // Clear the form fields after successful submission
      setName('');
      setDescription('');
      setImage('');
      setCollege('');
      setSubject('');
      setLocation('');
      setMode('');
      setMobileNumber('');
      setEmail('');
      setExperience('');
      setQrCode('');
      setTransaction('');
      setAgree(false);
    } catch (error) {
      console.error('Error saving user data:', error);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check if the file size is less than or equal to 25KB (25 * 1024 bytes)
      if (file.size <= 25 * 1024) {
        const reader = new FileReader();
        reader.onload = (event) => {
          const base64Image = event.target.result;
          setImage(base64Image);
        };
        reader.readAsDataURL(file);
      } else {
        alert('Image size should be less than or equal to 25KB');
      }
    }
  };

  return (
    <div>
      <div className="row justify-content-center mt-5">
        <div className="col-md-5">
          <div className="bs">
            <h2>Register</h2>
            <input
              type="text"
              className="form-control"
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              className="form-control"
              placeholder="Teacher Description - college"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              style={{ resize: 'none' }}
            />
            <input
              type="file"
              accept=".jpg, .jpeg, .png"
              onChange={handleImageChange}
              placeholder="Profile Picture"
            />
            {image && (
              <img
                src={image}  // Use the base64 data directly
                alt="Profile Pic"
                width="100"
              />
            )}

            <input
              type="text"
              className="form-control"
              placeholder="college (Highest Degree)"
              value={college}
              onChange={(e) => setCollege(e.target.value)}
            />
            <select
              className="form-control"
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
            >
              <option value="">Select Subject</option>
              <option value="physics">Physics</option>
              <option value="chemistry">Chemistry</option>
              {/* Add more subject options as needed */}
            </select>

            <input
              type="text"
              className="form-control"
              placeholder="Location"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
            />
            <select
              className="form-control"
              value={mode}
              onChange={(e) => setMode(e.target.value)}
            >
              <option value="">Select Mode</option>
              <option value="Offline">Offline</option>
              <option value="Online">Online</option>
            </select>
            <input
              type="text"
              className="form-control"
              placeholder="Mobile Number"
              value={mobileNumber}
              onChange={(e) => setMobileNumber(e.target.value)}
            />
            <input
              type="text"
              className="form-control"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              type="text"
              className="form-control"
              placeholder="Experience"
              value={experience}
              onChange={(e) => setExperience(e.target.value)}
            />
            <img
              className="qr-code"
              src={qrCode}
              alt="QR Code"
              width="200"
            />
            <input
              type="text"
              className="form-control"
              placeholder="Transaction ID by Paying Rs.49"
              value={transaction}
              onChange={(e) => setTransaction(e.target.value)}
            />
            <div className="form-check">
              <input
                type="checkbox"
                className="form-check-input"
                checked={agree}
                onChange={() => setAgree(!agree)}
              />
              <label className="form-check-label">
                I hereby confirm to share my details with Apni Vidya
              </label>
            </div>
            <button className="btn btn-primary mt-3" onClick={handleSubmit}>
              Register
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserForm;
