import React, { useState } from 'react';
import axios from 'axios';

const UserForm = () => {
  const [name, setName] = useState('');
  const [address, setAddress] = useState('');
  const [email, setEmail] = useState('');
  const [mobileNumber, setMobileNumber] = useState('');
  const [imageLink, setImageLink] = useState(''); // New state for the image link

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http://localhost:5000/api/users', {
        name,
        address,
        email,
        mobileNumber,
        image: imageLink, // Include the image link in the request
      });

      console.log('User data saved:', response.data);
      // Clear the form fields after successful submission
      setName('');
      setAddress('');
      setEmail('');
      setMobileNumber('');
      setImageLink('');
    } catch (error) {
      console.error('Error saving user data:', error);
    }
  };

  return (
    <div>
      <h2>Enter User Information</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Address:</label>
          <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
        </div>
        <div>
          <label>Mobile Number:</label>
          <input type="tel" value={mobileNumber} onChange={(e) => setMobileNumber(e.target.value)} />
        </div>
        <div>
          <label>Image Link:</label>
          <input type="text" value={imageLink} onChange={(e) => setImageLink(e.target.value)} />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default UserForm;
